// Ëé∑Âèñenv variable
// require('dotenv').config();

const { alchemyKey } = require('../../appconfig.js');


// ÂàõÂª∫ web3 provider.
const { createAlchemyWeb3 } = require("@alch/alchemy-web3");
const web3 = createAlchemyWeb3(alchemyKey);

// ÂàõÂª∫ contract web3 instance.
//    1. contract abi
//¬∑¬∑¬∑¬∑2. contract addressÔºå ÂéªÂëäËØâweb3.js Â∫ì Â¶Ç‰ΩïË∑üÊàë‰ª¨Êô∫ËÉΩÂêàÁ∫¶ËøõË°å‰∫§‰∫í„ÄÇ
const contractABI = require("../assets/tintinToken-abi.json");
const contractAddress = "0x015d485485D26faaEd45b33bF2B5a48621eF5D1b";
export const TinTinTokenContract = new web3.eth.Contract(
	contractABI,
	contractAddress
);


// ‰ªéÊô∫ËÉΩÂêàÁ∫¶ËØªÂèñÊï∞ÊçÆ
export const loadTokenName = async () => {
	const tokenName = await TinTinTokenContract.methods.name().call();
	return tokenName;
};

export const loadTokenAccountBalance = async (account) => {
	const balance = await TinTinTokenContract.methods.balanceOf(account).call();
	return +balance / 10 ** 18;
};


export const loadTotalSupply = async () => {
	const totalSupply = await TinTinTokenContract.methods.totalSupply().call();
	return +totalSupply / 10 ** 18
}

// ÈìæÊé•metamask Èí±ÂåÖ
export const connectWallet = async () => {
	if (window.ethereum) {
		try {
			const addressArray = await window.ethereum.request({
				method: "eth_requestAccounts",
			});
			const obj = {
				status: "üëÜüèΩ input the transfer to addresst in the text-field above.",
				address: addressArray[0],
			};
			return obj;
		} catch (err) {
			return {
				address: "",
				status: "üò• " + err.message,
			};
		}
	} else {
		return {
			address: "",
			status: (
				<span>
					<p>
						{" "}
						ü¶ä{" "}
						<a target="_blank" href={`https://metamask.io/download.html`}>
							You must install Metamask, a virtual Ethereum wallet, in your
							browser.
						</a>
					</p>
				</span>
			),
		};
	}
};
// Ëé∑ÂèñÈìæÊé•Èí±ÂåÖÂú∞ÂùÄ
export const getCurrentWalletConnected = async () => {
	if (window.ethereum) {
		try {
			const addressArray = await window.ethereum.request({
				method: "eth_accounts",
			});
			if (addressArray.length > 0) {
				return {
					address: addressArray[0],
					status: "üëÜüèΩ input the transfer to addresst in the text-field above.",
				};
			} else {
				return {
					address: "",
					status: "ü¶ä Connect to Metamask using the top right button.",
				};
			}
		} catch (err) {
			return {
				address: "",
				status: "üò• " + err.message,
			};
		}
	} else {
		return {
			address: "",
			status: (
				<span>
					<p>
						{" "}
						ü¶ä{" "}
						<a target="_blank" href={`https://metamask.io/download.html`}>
							You must install Metamask, a virtual Ethereum wallet, in your
							browser.
						</a>
					</p>
				</span>
			),
		};
	}
};

// ÂèëËµ∑‰∫§Êòì
export const transferToken = async (fromAddress, toAddress) => {
	let value = (10 ** 18).toFixed(0);
	console.log(value);
	//input error handling
	if (!window.ethereum || fromAddress === null) {
		return {
			status:
				"üí° Connect your Metamask wallet to update the message on the blockchain.",
		};
	}

	if (toAddress.trim() === "") {
		return {
			status: "‚ùå Your message cannot be an empty string.",
		};
	}

	//set up transaction parameters
	const transactionParameters = {
		to: contractAddress, // Required except during contract publications.
		from: fromAddress, // must match user's active address.
		data: TinTinTokenContract.methods.transfer(toAddress, value).encodeABI(),
	};

	//sign the transaction
	try {
		const txHash = await window.ethereum.request({
			method: "eth_sendTransaction",
			params: [transactionParameters],
		});
		return {
			status: (
				<span>
					‚úÖ{" "}
					<a target="_blank" href={`https://goerli.etherscan.io/tx/${txHash}`}>
						View the status of your transaction on Etherscan!
					</a>
					<br />
					‚ÑπÔ∏è Once the transaction is verified by the network, the token balance
					will be updated automatically.
				</span>
			),
		};
	} catch (error) {
		return {
			status: "üò• " + error.message,
		};
	}
};
