import React from "react";
import { useEffect, useState } from "react";
import {
	TinTinTokenContract,
	connectWallet,
	transferToken,
	loadTokenName,
	loadTokenAccountBalance,
	getCurrentWalletConnected,
} from "./util/web3interact.js";

import TinTinlogo from "./assets/tintinLogo.jpeg";

const TinTinToken = () => {
	const [walletAddress, setWallet] = useState("");                     // ËøûÊé•Èí±ÂåÖÁöÑÂú∞ÂùÄ
	const [status, setStatus] = useState("");                            // Áä∂ÊÄÅ‰ø°ÊÅØÔºåÂëäËØâuser Áé∞Âú®ÁöÑ‰∫§‰∫íÁä∂ÊÄÅÔºå
	const [tokenName, setTokenName] = useState("No connection.");        // TokenÂêçÁß∞
	const [tokenBalance, settokenBalance] = useState("No connection.");  // Êô∫ËÉΩÂêàÁ∫¶‰∫§‰∫íÊó∂ÔºåËé∑ÂèñÁöÑ‰ø°ÊÅØ„ÄÇ
	const [toAddress, setToAddress] = useState("");                      // ÂèëÈÄÅtoken Êé•Êî∂ÊñπÁöÑÂú∞ÂùÄ
	const [totalSupply, setTotalSupply] = useState(0);

	// ÂΩìwalletAddressÂÄºÂèòÂåñÊó∂ÔºåÂ∞±‰ºöÊâßË°å„ÄÇ 
	useEffect(() => {
		async function fetchData() {
			if (walletAddress !== "") {
				const tokenBalance = await loadTokenAccountBalance(walletAddress);
				settokenBalance(tokenBalance);
			}
			const tokenName = await loadTokenName();
			setTokenName(tokenName);
			const { address, status } = await getCurrentWalletConnected();
			setWallet(address);
			setStatus(status);
			addWalletListener();
			addSmartContractListener();
		}
		fetchData();
	}, [walletAddress]);

  // ÁõëÂê¨metamask ËøûÊé•Ë¥¶Êà∑ÁöÑÊîπÂèòÔºåÁÑ∂ÂêéÂÆûÊó∂ÊòæÁ§∫Âú®ÂâçÁ´Ø„ÄÇ
	function addWalletListener() {
		if (window.ethereum) {
			window.ethereum.on("accountsChanged", (accounts) => {
				if (accounts.length > 0) {
					setWallet(accounts[0]);
					setStatus(
						"üëÜüèΩ input the transfer to addresst in the text-field above."
					);
				} else {
					setWallet("");
					setStatus("ü¶ä Connect to Metamask using the top right button.");
				}
			});
		} else {
			setStatus(
				<p>
					{" "}
					ü¶ä{" "}
					<a target="_blank" href={`https://metamask.io/download.html`}>
						You must install Metamask, a virtual Ethereum wallet, in your
						browser.
					</a>
				</p>
			);
		}
	}

  // ÁõëÂê¨ Êô∫ËÉΩÂêàÁ∫¶
	function addSmartContractListener() {
		TinTinTokenContract.events.Transfer({}, (error, data) => {
			console.log(data);
			if (error) {
				setStatus("üò• " + error.message);
			} else {
				setToAddress("");
				setStatus("token transfer completed");
			}
		});
	}

  // ËøûÊé•Èí±ÂåÖÊåâÈíÆË¢´ÁÇπÂáªÊó∂ÂÄôÔºåÊâßË°åËøûÊé•Èí±ÂåÖÔºåÂπ∂Êõ¥Êñ∞Áä∂ÊÄÅ„ÄÇ
	const connectWalletPressed = async () => {
		const walletResponse = await connectWallet();
		setStatus(walletResponse.status);
		setWallet(walletResponse.address);
	};

  // ÂèëÈÄÅtokenÔºåÂπ∂Êõ¥Êñ∞Áä∂ÊÄÅ„ÄÇ
	const onTransferPressed = async () => {
		const { status } = await transferToken(walletAddress, toAddress);
		setStatus(status);
	};

	//the UI of our component
	return (
		<div id="container">
			<img id="logo" src={TinTinlogo}></img>
			<button id="walletButton" onClick={connectWalletPressed}>
				{walletAddress.length > 0 ? (
					"Connected: " +
					String(walletAddress).substring(0, 6) +
					"..." +
					String(walletAddress).substring(38)
				) : (
					<span>Connect Wallet</span>
				)}
			</button>

			<h2 style={{ paddingTop: "50px" }}>Token Name:</h2>
			<p>{tokenName}</p>

			<h2 style={{ paddingTop: "50px" }}>Balance:</h2>
			<p>{tokenBalance}</p>

			<h2 style={{ paddingTop: "50px" }}>Total Supply:</h2>
			<p>{tokenBalance}</p>

			<h2 style={{ paddingTop: "18px" }}>Transfer 1 TinTin Token To:</h2>

			<div>
				<input
					type="text"
					placeholder="transfer token to:"
					onChange={(e) => setToAddress(e.target.value)}
					value={toAddress}
				/>
				<p id="status">{status}</p>

				<button id="publish" onClick={onTransferPressed}>
					Transfer
				</button>
			</div>
		</div>
	);
};

export default TinTinToken;
